// Utility for critical resource loading
export const preloadCriticalAssets = () => {
  if (typeof window !== 'undefined') {
    // Create link elements for critical assets
    const criticalAssets = [
      '/main/profile.jpeg',
      '/main/profile_mobile.jpeg',
      '/bg/jochen-buckers-tIsz0EbqZHc-unsplash.jpg',
      '/bg/rosie-fraser-1L71sPT5XKc-unsplash.jpg'
    ];

    criticalAssets.forEach(asset => {
      const link = document.createElement('link');
      link.rel = 'preload';
      link.as = 'image';
      link.href = asset;
      document.head.appendChild(link);
    });
  }
};

// Image optimization utility
export const getOptimizedImageProps = (src: string, width: number, height: number, priority = false) => ({
  src,
  width,
  height,
  quality: 80,
  priority,
  placeholder: 'blur' as const,
  blurDataURL: 'data:image/jpeg;base64,'
});
